mapper.update:

	update tb_sign
       set si_status = 'Y',
           si_appdate = current_date
     where dp_no = #{ dpNo }
       and signemp_no = #{ signEmpNo }


	update tb_sign
       set si_status = 'R',
           si_appdate = current_date
     where dp_no = #{ dpNo }
       and signemp_no = #{ signEmpNo }

		update tb_dtpaper
		   set dp_title = #{dpTitle}

		     , dp_final = '대기'
		     , dp_create = current_date

		      ,dp_origin = #{dpOrigin}
		      ,dp_change = #{dpChange}

		      ,dp_modify = current_date

 		 where dp_no = #{dpNo}

update tb_vacation
   set va_start = #{vaStart}
