mapper.insert:
insert 
	into tb_position
		(
			pos_cd
		  , pos_name
		)
		values
		(
			#{positionCode}
		  , #{positionName}
		)

insert 
	into tb_department
		(
			dep_cd
		  , dep_name
		)
		values
		(
			#{departmentCode}
		  , #{departmentName}
		)

mapper.update:
update tb_position
	set 
		pos_cd = #{positionCode2}
	   ,pos_name = #{positionName2}
	where pos_cd = #{positionCode1}
	  and pos_name = #{positionName1}

update tb_department
	set 
		dep_cd = #{departmentCode2}
	   ,dep_name = #{departmentName2}
	where dep_cd = #{departmentCode1}
	  and dep_name = #{departmentName1}

mapper.insert:
insert 
	into tb_holiday1
		(
			hol1_day
		  , hol1_name
		)
		values
		(
			#{holidayCode}
		  , #{holidayName}
		)

mapper.update:
update tb_holiday1
	set 
		hol1_day = #{holidayCode2}
	   ,hol1_name = #{holidayName2}
	where hol1_day = #{holidayCode1}
	  and hol1_name = #{holidayName1}
