mapper.insert:
insert
  into tb_book
  (
    bk_no
  , emp_no
  , rm_no
  , bk_person
  , bk_title
  , bk_meetdate
  , bk_start
  , bk_end
  )
  values
  (
  	seq_bkno.nextval
  , #{ empNo }
  , #{ rmNo }
  , #{ bkPerson }
  , #{ bkTitle }
  , #{ bkDate }
  , #{ bkStart }
  , #{ bkEnd }
  )

mapper.update:
update
	   tb_book
   set 
	    bk_person = #{ bkPerson }
	  , bk_title = #{ bkTitle }
	  , bk_meetdate = #{ bkDate }
	  , bk_start = #{ bkStart }
	  , bk_end = #{ bkEnd }
	  , bk_modify = current_date  
  where bk_no = #{ bkNo }

mapper.select:
select
       count(*)
  from tb_book
  join tb_room using(rm_no)
 where rm_no = #{ rmNo }
   and bk_meetdate between #{ start } and #{ end }
