mapper.insert:
 		INSERT
		  INTO TB_DUTY
		VALUES
		     (
		       SEQ_DNO.NEXTVAL
		     , #{empNo}
		     , #{empName}
		     , #{calendarYN}
		     , #{importance}
		     , #{progress}
		     , #{title}
		     , #{content}
		     , DEFAULT
		     , #{startDate}
		     , #{endDate}
		     , 'Y'
		     ,#{filePath}
		     )

INSERT
  INTO TB_FILE
VALUES
	 (
	   SEQ_FILENO.NEXTVAL
	 , #{refNo} 
	 , 1
	 , #{fileOriginName}
	 , #{fileChangeName}
	 , #{filePath}
	 , DEFAULT
	 , NULL
	 , 'Y'
	 )

mapper.select:
		SELECT COUNT(COUNT(*))
		  FROM TB_DUTY D
		  JOIN TB_DUTY_CHARGE DC
		 USING (DUTY_NO)
		 WHERE STATUS = 'Y'


		 	AND D.EMP_NO = #{empNo}


		 	AND DC.EMP_NO = #{empNo}


		 	AND DUTY_NO NOT IN ${dutyNo}


			 	AND PROGRESS = #{progress}
 			AND TITLE LIKE #{title}


 			AND TITLE LIKE #{title}


			AND D.EMP_NO = #{empNo}
		    OR DC.EMP_NO = #{empNo}


         GROUP BY DUTY_NO

SELECT A.* , D.CONTENT FROM ( SELECT DISTINCT D.DUTY_NO AS "DUTY_NO" , D.EMP_NO AS "EMP_NO" , D.EMP_NAME AS "EMP_NAME" , CALENDAR_YN , IMPORTANCE , PROGRESS , TITLE , ENROLL_DATE , START_DATE , END_DATE , STATUS FROM TB_DUTY D JOIN TB_DUTY_CHARGE DC ON D.DUTY_NO = DC.DUTY_NO WHERE STATUS = 'Y'
AND DC.EMP_NO = #{empNo}
AND D.EMP_NO = #{empNo}
AND D.EMP_NO = #{empNo} OR DC.EMP_NO = #{empNo}
) A JOIN TB_DUTY D ON A.DUTY_NO = D.DUTY_NO
WHERE D.DUTY_NO NOT IN ${dutyNo}
WHERE A.PROGRESS = #{progress} AND A.TITLE LIKE #{title}
WHERE A.TITLE LIKE #{title}
ORDER BY D.DUTY_NO DESC

mapper.update:
	UPDATE TB_DUTY
	   SET CALENDAR_YN = #{calendarYN}
	     , IMPORTANCE = #{importance}
	     , PROGRESS = #{progress}
	     , TITLE = #{title}
	     , CONTENT = #{content}
	     , START_DATE = #{startDate}
	     , END_DATE = #{endDate}

	  	 , FILE_PATH = #{filePath} 

     WHERE DUTY_NO = #{dutyNo}

	UPDATE TB_FILE
	   SET FILE_ORIGIN_NAME = #{fileOriginName}
	  	 , FILE_CHANGE_NAME = #{fileChangeName}
	  	 , FILE_PATH = #{filePath}
	  	 , FILE_UPLOAD_DATE = CURRENT_DATE
  	 WHERE FILE_REF_NO = #{refNo}
  	   AND FILE_CATEGORY = 1

UPDATE TB_DUTY
   SET STATUS = 'N'
 WHERE DUTY_NO = #{dutyNo}
