mapper.insert:
insert 
  into tb_employee
  ( 
    emp_no
  , emp_id
  , emp_pwd
  , emp_name
  , emp_email
  , emp_nt_admin
  , dep_cd
  , pos_cd
  , emp_enrolldate
  , emp_extension
  , emp_phone
  , emp_profile
  )
  values
  (



    SUBSTR(#{empEnrollDate}, 3, 2) || LPAD(#{depCd}, 2, '0') || LPAD(seq_empno.nextval, 3, '0')
  , #{empId}
  , #{empPwd}
  , #{empName}
  , #{empId} || '@workpartner.com'	


  	, 'A'


  	, default


  , #{depCd}
  , #{posCd}
  , #{empEnrollDate}
  , #{empExtension}
  , #{empPhone}  
  , #{empProfile}
  )

mapper.update:
	update
		   tb_employee
	   set
    	   emp_phone = #{empPhone}
    	 , emp_extension = #{empExtension}
    	 , emp_retiredate = #{empRetireDate}
    	 , emp_status = #{empStatus}

    	 , dep_cd = #{depCd}


	  	 , emp_nt_admin ='A'


	  	 , emp_nt_admin = default


    	 , pos_cd = #{posCd}
    	 , emp_modifydate = default
     where emp_id = #{empId}
