mapper.insert:
	insert 
	  into tb_signature
	 	 (
		  	sig_no
          , emp_no		      
          , sig_name
          , sig_dep
          , sig_position
          , sig_address           
          , sig_extension  
          , sig_tel
          , sig_email          	  
	 	 )
		  values
		  (
		    SEQ_SIGNO.nextval
		  , #{empNo}
		  , #{sigName}
		  , #{sigDepartment}
		  , #{sigPosition}
		  , #{sigAddress}
		  , #{sigExtension}
		  , #{sigTel}
		  , #{sigEmail}
		  )

mapper.update:
update 
	   tb_employee
   set emp_sig_use = #{empSigUse}
 where emp_no = #{empNo}

mapper.insert:
insert
  into tb_mail
  		(
	  		mail_no,
			mail_title,
			mail_content,
			mail_important,
			mail_security,
			mail_file,
			mail_recipient,
			mail_cc,
			mail_sender,
			mail_sender_name	
  		)
  values
  		(
  		    SEQ_MANO.nextval
  		  , #{mailTitle} 
  		  , #{mailContent} 
  		  , #{mailImportant} 
  		  , #{mailSecurity} 
  		  , #{mailFile} 
  		  , #{mailRecipient} 
  		  , #{mailCC} 
  		  , #{mailSender} 
  		  , #{mailSenderName} 		
  		)

update.foreach:

update 
	   tb_mail_status
   set mail_status = 'N'
 where
 		mail_no = #{m.mailNo}


		 	and	mail_email = #{m.mailEmail}	


 			and mail_email IS NULL


 	and mail_category = #{m.mailCategory}


update 
	   tb_mail_status
   set mail_read = 'Y'
 where
 		mail_no = #{m.mailNo}


		 	and	mail_email = #{m.mailEmail}	


 			and mail_email IS NULL 


 	and mail_category = #{m.mailCategory}


update 
	   tb_mail_status
   set mail_star = 'Y'
 where
 		mail_no = #{m.mailNo}


		 	and	mail_email = #{m.mailEmail}	


 			and mail_email IS NULL 


 	and mail_category = #{m.mailCategory}


update 
	   tb_mail_status
   set mail_star = 'N'
 where
 		mail_no = #{m.mailNo}


		 	and	mail_email = #{m.mailEmail}	


 			and mail_email IS NULL 


 	and mail_category = #{m.mailCategory}


delete 
  from tb_mail_status
 where
 	   mail_no = #{m.mailNo}


		 	and	mail_email = #{m.mailEmail}	


 			and mail_email IS NULL 


 	and mail_category = #{m.mailCategory}


update 
	   tb_mail_status
   set mail_status = 'Y'
 where
 		mail_no = #{m.mailNo}


		 	and	mail_email = #{m.mailEmail}	


 			and mail_email IS NULL


 	and mail_category = #{m.mailCategory}

mapper.update:
update 
	   tb_mail_status
   set mail_read = 'Y'
 where mail_email = #{mailEmail}
   and mail_no = #{mailNo}
