mapper.insert:
		insert
		  into tb_notice
		  	(
 	            nt_no
              , nt_title
              , nt_content
              , emp_no
              , emp_name              
              , nt_important
              , nt_top             
              , nt_file 	
		  	)
		 values
		 	(
		 		SEQ_NTNO.NEXTVAL
		 	  , #{noticeTitle}
		 	  , #{noticeContent}
		 	  , #{empNo}
		 	  , #{empName}
		 	  , #{noticeImportant}
		 	  , #{noticeTop}
		 	  , #{noticeFile}	
		 	)

mapper.update:
		 update 
				 tb_notice
		    set  nt_title = #{noticeTitle}
               , nt_content = #{noticeContent}
               , nt_important = #{noticeImportant}
               , nt_top = #{noticeTop}
               , nt_modify_date = sysdate
               , nt_file = #{noticeFile}  
		  where  nt_no = ${noticeNo}

 	update
	        tb_file
	    set file_status = 'Y'    
	  where file_ref_no = #{refNo}
	    and file_category = 4  
	    and file_no = #{fileNo}

mapper.insert:
insert
  into  tb_file
     (
    	file_no
      , file_ref_no
      , file_category	
      , file_origin_name
      , file_change_name
      , file_path		      
   	 )		    
values
  	 (
  	 	SEQ_FILENO.nextval
  	  , #{refNo}
  	  , 4
  	  , #{fileOriginName}
  	  , #{fileChangeName}
  	  , #{filePath}		  	
  	 )
