mapper.insert:
INSERT
  INTO TB_PROJECT
VALUES
	 (
	   SEQ_PNO.NEXTVAL
	 , #{projTitle}
	 , #{empNo}
	 , #{progress}
	 , #{startDate}
	 , #{endDate}
	 , DEFAULT
	 , DEFAULT 
	 )

mapper.update:
UPDATE TB_PROJECT
   SET PROJ_TITLE = #{projTitle}
   	 , PROGRESS = #{progress}
   	 , START_DATE = #{startDate}
   	 , END_DATE = #{endDate}
 WHERE PROJ_NO = #{projNo}

UPDATE TB_PROJECT
   SET STATUS = 'N'
 WHERE PROJ_NO = #{projNo}

UPDATE TB_PROJECT_MEM
   SET MEM_ROLE = #{memRole}
 WHERE PROJ_NO = #{projNo}
   AND MEM_NO = #{memNo}

mapper.delete:
UPDATE TB_PROJECT_MEM
   SET STATUS = 'N'
 WHERE PROJ_NO = #{projNo}
   AND MEM_NO = #{memNo}
