INSERT
  INTO TB_FILE
VALUES
     (
       SEQ_FILENO.NEXTVAL
     , #{refNo}
     , 2
     , #{fileOriginName}
     , #{fileChangeName}
     , #{filePath}
     , DEFAULT
     , DEFAULT
     , DEFAULT
     )

mapper.update:
UPDATE TB_PBOARD
   SET TITLE = #{title}
   	 , CONTENT = #{content}
   	 , MODIFY_DATE = DEFAULT
 WHERE PBOARD_NO = #{pboardNo}

UPDATE TB_PDUTY
   SET PROGRESS = #{progress}
     , START_DATE = #{startDate}
     , END_DATE = #{endDate}
     , IN_CHARGE = #{incharge}
     , IN_CHARGE_NAME = #{inchargeName}
     , CALENDAR_YN = #{calendarYN}
 WHERE PBOARD_NO = #{pboardNo}

UPDATE TB_FILE
   SET FILE_ORIGIN_NAME = #{fileOriginName}
     , FILE_CHANGE_NAME = #{fileChangeName}
     , FILE_PATH = #{filePath}
     , FILE_UPLOAD_DATE = DEFAULT
 WHERE FILE_REF_NO = #{refNo}
   AND FILE_CATEGORY = 2

UPDATE TB_PBOARD
   SET STATUS = 'N'
 WHERE PBOARD_NO = #{pboardNo}

